package SiecPrzeplywowa;import com.google.gson.*;import java.io.File;import java.io.FileNotFoundException;import java.io.FileReader;import java.io.Reader;import static java.lang.Integer.MIN_VALUE;public class Main {    public static void main(String[] args) throws FileNotFoundException {        Siec S = new Siec();        S.addEdge(11, 483, 274, 469, 328);        S.addEdge(13, 284, 107, 238, 236);        S.addEdge(13, 521, 406, 556, 358);        S.addEdge(0, 521, 406, 470, 489);        S.addEdge(8, 238, 236, 128, 242);        S.addEdge(10, 238, 236, 351, 306);        S.printGraph();        System.out.println();        Gson gson = new Gson();        int maxIndex = MIN_VALUE;        JsonObject obj = (JsonObject) JsonParser.parseReader(new FileReader("data.json"));        JsonArray roads = obj.getAsJsonArray("roads");        Siec S = new Siec(666);        for (JsonElement road : roads) {            JsonObject roadObj = road.getAsJsonObject();            int maxFlow = roadObj.get("maxBarleyFlow").getAsInt();            int a = roadObj.get("x1").getAsInt();            if (maxIndex < a){                maxIndex = a;            }            int b = roadObj.get("y1").getAsInt();            if (maxIndex < b){                maxIndex = b;            }            S.addEdge(maxFlow, a, b);        }        }        }